$("#products").on("change", function(e) {
    let selected = $(this).find('option:selected', this);
    let input = [];
    let hasCounter = false;
    selected.each(function() {
        if($(this).attr('data-hasCounter') == 1) {
            hasCounter = true;
        }
    });
    if(hasCounter) {
        let ipt = "<div class='row'>"+
            "<div class='col-12 mb-3'>" +
            "<div class='form-floating'>" +
            "<input type='number' step='1' name='counter' class='form-control' placeholder='Kontör Adedi'>"+
            "<label class='form-label'>Kontör Adedi</label>" +
            "</div>" +
            "</div>" +
            "</div>";
        input.push(ipt);
    }

    $("#kontor_holder").html(input);
})
